import React, { Component } from 'react';
import axios from 'axios';
import logo from './logo.svg';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends Component {

  state = {
    image: null,
    image2: null,
    processedImageData: null,
    processedImageData2: null,
  };



  handleImageChange = (e) => {
    this.setState({
      image: e.target.files[0],
      image2: e.target.files[0]
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    if (this.state.image) {
      let form_data = new FormData();
      form_data.append('image', this.state.image, this.state.image.name);

      axios.post('http://localhost:8000/resnet50_unet/', form_data, {
        headers: {
          'content-type': 'multipart/form-data',
        },
        responseType: 'arraybuffer', // Request binary data
      })
        .then((res) => {
          // Convert the binary data to a base64 string
          const base64Data = btoa(
            new Uint8Array(res.data).reduce(
              (data, byte) => data + String.fromCharCode(byte),
              ''
            )
          );
          const processedImageData = `data:image/png;base64,${base64Data}`;
          this.setState({ processedImageData });
        })
        .catch((err) => console.log(err));
    } else {
      console.log('No image selected');
    }

    let form_data = new FormData();
    form_data.append('image', this.state.image, this.state.image.name);
      axios.post('http://localhost:8000/vgg16_unet/', form_data, {
        headers: {
          'content-type': 'multipart/form-data',
        },
        responseType: 'arraybuffer', // Request binary data
      })
        .then((res) => {
          // Convert the binary data to a base64 string
          const base64Data = btoa(
            new Uint8Array(res.data).reduce(
              (data, byte) => data + String.fromCharCode(byte),
              ''
            )
          );
          const processedImageData2 = `data:image/png;base64,${base64Data}`;
          this.setState({ processedImageData2});
        })
        .catch((err) => console.log(err));
  };

  render() {
    return (
      <div className="App">
        <h1>MEMIE HMMHMMM AJI HMM</h1>
        <div class="media">
  <img class="d-flex align-self-center mr-3" src={logo} alt="Generic placeholder image"/>
  <div class="media-body">
    <h5 class="mt-0">Center-aligned media</h5>
    <p>Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate fringilla. Donec lacinia congue felis in faucibus.</p>
    <p class="mb-0">Donec sed odio dui. Nullam quis risus eget urna mollis ornare vel eu leo. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.</p>
  </div>
</div>


        <form onSubmit={this.handleSubmit}>
          <p>
            <input
              type="file"
              id="image"
              accept="image/png, image/jpeg"
              onChange={this.handleImageChange}
              required
            />
          </p>
          <input type="submit" value="Upload Image" />
        </form>
        {this.state.processedImageData2 && (
          <img src={this.state.processedImageData2} alt="Processed Image" />

        )}





      </div>
    );
  }
}

export default App;
